import React, { useState } from "react";
import { AddIcon, ChevronDownIcon } from "@chakra-ui/icons";
import {
  Menu,
  MenuButton,
  MenuList,
  MenuItem,
  MenuItemOption,
  MenuGroup,
  MenuOptionGroup,
  MenuDivider,
} from "@chakra-ui/react";

const SelectCourse = () => {
  return (
    <Menu className="border-2 border-gray-400 ">
      <MenuButton rightIcon={<ChevronDownIcon />}>Actions</MenuButton>
      <MenuList>
        <MenuItem>Download</MenuItem>
        <MenuItem>Create a Copy</MenuItem>
        <MenuItem>Mark as Draft</MenuItem>
        <MenuItem>Delete</MenuItem>
        <MenuItem>Attend a Workshop</MenuItem>
      </MenuList>
    </Menu>
  );
};

const Studentreg = () => {
  const [courseCount, setcourseCount] = useState(0);

  const renderButton = () => {
    for (let i = 0; i <= courseCount; i++) {
      return <SelectCourse />;
    }
  };
  const hello = () => {
    console.log("sasas");
    setcourseCount(courseCount + 1);
    return <SelectCourse />;
  };
  return (
    <div className=" w-1/2 rounded-lg bg-gray-200 ">
      <div className="h-96  border-black border-0 pt-3 ">
        <div className=" flex justify-center">
          <h1 className="border-1 border-black text-lg justify-center mb-2">
            Register Student
          </h1>
        </div>
        <div className="px-32">
          <div className="flex flex-row align-baseline justify-center">
            <h1 className="w-fit">First Name</h1>
            <input className="bg-transparent border-2 border-gray-300 rounded-lg w-60 mb-4 mx-4 hover:border-gray-400 px-2"></input>
          </div>
          <div className="flex flex-row align-baseline justify-center">
            <h1 className="w-fit">Last Name</h1>
            <input className="bg-transparent border-2 border-gray-300 rounded-lg w-60 mb-4 mx-4 hover:border-gray-400 px-2"></input>
          </div>
          <div className="flex flex-row align-baseline justify-center">
            <h1 className="w-fit mr-5">E - mail</h1>
            <input className="bg-transparent border-2 border-gray-300 rounded-lg w-60 mb-4 mx-4 hover:border-gray-400 px-2"></input>
          </div>
          <div className="flex flex-row align-top justify-center">
            <h1 className="w-fit mr-5">Courses</h1>
            <AddIcon
              w={6}
              h={6}
              onClick={hello}
              className="rounded-xl p-1 transition-all duration-300 hover:bg-gray-400"
            />
          </div>
          <div className="flex flex-row align-top justify-center ">
            {renderButton()}
            <SelectCourse />
          </div>
        </div>
      </div>
    </div>
  );
};

export default Studentreg;
