import React, { useState } from "react";
import { Tag } from "@chakra-ui/react";
import { useToast } from "@chakra-ui/react";
const Studentreg = () => {
  const toast = useToast();
  const test = () => {
    console.log(course);
    return toast({
      title: "Student Registered",
      description: "Studnet has been registered succufully",
      status: "success",
      duration: 4000,
      isClosable: true,
    });
  };

  const [course, setCourse] = useState([]);

  const SelectCourse = () => {
    const handleChange = (e) => {
      if (!course.includes(e.target.value))
        setCourse([...course, e.target.value]);
    };
    return (
      <select
        onChange={handleChange}
        className="bg-transparent border-2 border-gray-300 rounded-lg hover:border-gray-400 p-1 w-32 m-auto mb-1"
      >
        <option value={null}>select</option>
        <option value={`DSA`}>DSA</option>
        <option value={`DBA`}>DBAb</option>
        <option value={`JAVA`}>Java</option>
      </select>
    );
  };

  return (
    <div className=" w-1/2 rounded-lg bg-gray-200 ">
      <div className="h-auto border-black border-0 pt-3 transition-all duration-700">
        <div className=" flex justify-center">
          <h1 className=" text-xl justify-center mb-2">Register Student</h1>
        </div>
        <div className="px-32">
          <div className="flex flex-row align-baseline justify-center">
            <h1 className="w-fit">First Name</h1>
            <input className="bg-transparent border-2 border-gray-300 rounded-lg w-60 mb-4 mx-4 hover:border-gray-400 px-2"></input>
          </div>
          <div className="flex flex-row align-baseline justify-center">
            <h1 className="w-fit">Last Name</h1>
            <input className="bg-transparent border-2 border-gray-300 rounded-lg w-60 mb-4 mx-4 hover:border-gray-400 px-2"></input>
          </div>
          <div className="flex flex-row align-baseline justify-center">
            <h1 className="w-fit mr-5">E - mail</h1>
            <input className="bg-transparent border-2 border-gray-300 rounded-lg w-60 mb-4 mx-4 hover:border-gray-400 px-2"></input>
          </div>
          <div className="flex flex-row align-top justify-center mb-2">
            <h1 className="w-fit mr-5">Courses</h1>
            {course ? (
              course.map((item) => <Tag className="mr-1">{`${item}`}</Tag>)
            ) : (
              <></>
            )}
          </div>
          <div className="flex flex-col align-top justify-center ">
            <SelectCourse />
          </div>
          <div className="flex flex-col justify-center ">
            <button
              className="m-auto h-8 mt-3 mb-3 border-2 rounded-lg hover:bg-gray-500 transition-all duration-700 border-gray-300 w-32"
              onClick={test}
            >
              <span className="hover:text-gray-200 transition-all duration-700">
                Register
              </span>
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Studentreg;
