import React, { useState, useEffect } from "react";
import SingleSearchResult from "./SingleSearchResult";
import axios from "axios";

const StudentSearch = () => {
  const [allStudent, setAllStudent] = useState([]);

  const fetachAllStudents = () => {
    axios.get("http://localhost:8080/api/v1/allStudents").then(
      (res) => {
        console.log(res);
        setAllStudent(res);
      },
      (error) => {
        console.log(error);
      }
    );
  };

  return (
    <div className="border-gray-500 h-max w-9/12 pt-10 pb-10 rounded-lg bg-gray-200">
      <div className=" h-96 flex flex-col ">
        <span className="border-0  border-black text-xl justify-center mb-4 h-10 m-auto mt-3 bg-gray-700 text-gray-200 pt-1 pl-6 pr-6 rounded-lg">
          Search Student
        </span>
        <div className="flex justify-center items-baseline">
          <input
            className="bg-transparent border-2 border-gray-300 rounded-lg w-60 mb-4 mx-4 hover:border-gray-400 px-2"
            placeholder="Search By name"
          ></input>
          <button className="h-8 border-2 rounded-lg transition-all duration-1000 hover:bg-gray-700 border-gray-400 pl-2 pr-2">
            <span className="hover:text-gray-200 transition-all duration-700">
              Search
            </span>
          </button>
        </div>

        <div className="border-2 border-gray-400 rounded-3xl h-1 bg-gray-400 m-4 mt-0"></div>
        <div>
          <div className="px-32  w-full border-2 border-black pl-0 ml-3 mr-3 flex flex-row justify-around items-baseline">
            <h1 className="font-semibold">First Name</h1>
            <h1 className="font-semibold">Last Name</h1>
            <h1 className="font-semibold">Email</h1>
            <h1 className="font-semibold">Courses</h1>
          </div>
          <SingleSearchResult />
        </div>
      </div>
    </div>
  );
};

export default StudentSearch;
