import React, { useState } from "react";
import { cookieStorageManager, useToast } from "@chakra-ui/react";

export const AddCourse = () => {
  const [courseName, setCourseName] = useState("");
  const [url, setUrl] = useState("");

  const toast = useToast();
  const handleAddButton = () => {
    return toast({
      title: "Course Added",
      description: "Course has been added successfully",
      status: "success",
      duration: 4000,
      isClosable: true,
    });
  };

  const fetachAddCourse = () => {};

  return (
    <div className=" w-1/2 h-min p-3 rounded-lg bg-gray-200 mr-2  flex justify-center flex-col ">
      <span className="border-1 border-black text-xl justify-center mb-5 m-auto bg-gray-700 pl-6 pr-6 pb-2 pt-1.5 rounded-lg text-gray-200">
        Add Course
      </span>

      <div>
        <div className="m-auto">
          <div className="flex flex-row align-baseline justify-center">
            <h1 className="w-28">Course Name</h1>
            <input
              className="bg-transparent border-2 border-gray-300 rounded-lg w-60 mb-4 mx-4 hover:border-gray-400 px-2"
              onInput={(e) => setCourseName(e.target.value)}
            ></input>
          </div>
          <div className="flex flex-row align-baseline justify-center">
            <h1 className="w-28">Url</h1>
            <input
              className="bg-transparent border-2 border-gray-300 rounded-lg w-60 mb-4 mx-4 hover:border-gray-400 px-2"
              onInput={(e) => setUrl(e.target.value)}
            ></input>
          </div>

          <div className="flex flex-col align-top justify-center "></div>
          <div className="flex flex-col justify-center ">
            <button className="m-auto mt-3 h-8 mb-3 border-2 rounded-lg hover:bg-gray-500 transition-all duration-700 border-gray-400 w-32 ">
              <span
                className="hover:text-gray-200 transition-all duration-700"
                onClick={handleAddButton}
              >
                Add
              </span>
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};
