import { useToast } from "@chakra-ui/react";
const toast = useToast();
const Toast = () => {
  return toast({
    title: "Course added",
    description: "Course added succufully",
    status: "success",
    duration: 4000,
    isClosable: true,
  });
};

export default Toast;
